import logo from './logo.svg';
import './App.css';
import image from "./image.jpg"
import { useState } from 'react';
import "./style.css"

function App() {
  let[hoverimage,sethoverimage]=useState(false)
  const[Loading,setLoading]=useState(false)

  const inner_card = {
    transition:"transform 0.6s",
    transform:hoverimage?"rotateY(180deg)":"rotateY(0deg)",
    transformStyle: "preserve-3d"
  }

  // const card
  function handleMouseover(){
    sethoverimage(true)
  }
  function handleMouseleave(){
    sethoverimage(false)
  }
  const inner_card_back = {
    transform:"rotateY(180deg)",
    color:"white"
  }


  // useEffect(()=>{
  //   setLoading(true);
  //   setInterval(()=>{

  //     setLoading(false)
  //   },1000)


  // },[])

  return (
    <div  className='flip-card'
    // style={flip-card}
    >
        <div className='flip-card-inner'
        // style={inner_card} 
        >
          <div className='flip-card-front'
          // style={inner_card_front}
          >
            <img src={image} alt="nature image" onMouseOver={handleMouseover} onMouseLeave={handleMouseleave}></img>
          </div>
            <div  className='flip-card-back'
            style={inner_card_back}
            >
                    <h1>Hello</h1>
            </div>
        </div>
    </div>
    // <div>

    // </div>

  );
}

export default App;
